SERVER_URL <- 'http://gfe.b12x.org/'


test_that("Get GFE from sequence data",{

	host  <- SERVER_URL
	seq   <- ""
	locus <- "HLA-A"
	gfe   <- seq2gfe(host, locus, seq)

	gfe.character <- as.character(unlist(gfe$gfe))
	aligned       <- as.numeric(unlist(gfe$aligned))
	expect_false(is.null(gfe))
	expect_true(is.list(gfe))

	expect_that(gfe.character, equals("HLA-Aw1-1-7-20-10-32-7-1-1-1-6-1-5-3-5-1-1"))
	expect_that(aligned, equals(1.0))

})
