SERVER_URL <- 'http://gfe.b12x.org/'


test_that("Get sequence from gfe",{

	host  <- SERVER_URL
	seq.e <- ""
	locus <- "HLA-A"
	gfe   <- "HLA-Aw1-1-7-20-10-32-7-1-1-1-6-1-5-3-5-1-1"
	seq   <- gfe2seq(host, locus, gfe)
	
	seq.character <- as.character(unlist(seq$sequence))

	expect_false(is.null(seq))
	expect_true(is.list(seq))

	expect_that(seq.character, equals(seq.e))

})
